content:
code {
    color:#ff502c;
    background-color: #fff5f5;
}
.move {
    width: 330px;
    position: absolute;
    top: 20px;
    right: 0;
    /* height: 450px; */
    display: block;
    z-index: 999;
    cursor: move;
}
.mask {
    position: absolute;
    cursor: move;
    user-select: none;
    width: 310px !important;
    height: 40px !important;
    top: 10px;
    left: 10px;
}
.close {
    position: absolute;
    cursor: move;
    user-select: none;
    width: 20px !important;
    height: 30px !important;
    top: 0px;
    right: 60px;
}
.close:after {
    content: 'X';
    position: absolute;
    right: -45px;
    color: black;
    top: 15px;
    z-index: 999999999;
    cursor: pointer;
}
#ip-map {
    display: none;
}
.live2d-widget-container {
    left: 330px;
}
